con:configuration.scriptText:
import groovy.json.JsonSlurper

//To get response headers
def headers =  messageExchange.getResponseHeaders()

//To get response status
def actualStatus =  headers["#status#"].get(0)
def expectedStatus = "HTTP/1.1 200 OK"

//To compare actual & expected response code
if(actualStatus.equals(expectedStatus))
 log.info "Response code is 200"
else 
 log.info "Response code is not 200"

assert expectedStatus==actualStatus

//To get response
def response = messageExchange.response.responseContent
def obj = new JsonSlurper().parseText(response)

//To fetch accessToken value from response
def accessToken = obj.data.access_token

//To set accessToken at testcase property level
context.testCase.testSuite.setPropertyValue("accessToken",accessToken)

