import jxl.*
import com.eviware.soapui.model.testsuite.TestRunner.Status

def productid,price,name,metatitle
//To access the path of file where product details are provided
def filepath = "C:\\Users\\PravalikaPuchakayala\\Desktop\\Pravalika\\FullStack\\userdetails.xls"
File Excel = new File(filepath)

Workbook wb = Workbook.getWorkbook(Excel)
Sheet sh = wb.getSheet("Sheet3")
//To get the row size
def rowsize = sh.getRows().toInteger()
log.info "$rowsize"


//Loop over each row and run the test case UpdateProducById
for(def i=1;i<rowsize;i++)
 {
 	 productid=sh.getCell(0,i).getContents()
 	 price = sh.getCell(1,i).getContents()
 	 name = sh.getCell(2,i).getContents()
      metatitle = sh.getCell(3,i).getContents()
      log.info "$productid + $price + $name + $metatitle"
 	 testRunner.testCase.setPropertyValue("productId",productid)
      testRunner.testCase.testSteps['UserDetails'].setPropertyValue("price",price)
      testRunner.testCase.testSteps['UserDetails'].setPropertyValue("name",name)
      testRunner.testCase.testSteps['UserDetails'].setPropertyValue("metatitle",metatitle)
      testRunner.runTestStepByName("UpdateProductById")

 }

