con:config.script:
//To get response content of DatabaseConnection test step 
def response = context.testCase.getTestStepByName('DatabaseConnection').getPropertyValue("responseAsXml")
log.info response

//To get the count of number of rows
def count = context.expand('${DatabaseConnection#ResponseAsXml#count(//ResultSet/Row)}')
log.info count

//Loop over each row and run the AddProduct test case with the products details retreived from db
for(def i=1;i<=Integer.parseInt(count);i++)
{
     //Get Product Details from DatabaseConnection response for the given row
	def model = context.expand('${DatabaseConnection#ResponseAsXml#//ResultSet/Row['+i+']/PRODUCTDETAILS.MODEL}')
     def quantity = context.expand('${DatabaseConnection#ResponseAsXml#//ResultSet/Row['+i+']/PRODUCTDETAILS.QUANTITY}')
     def price = context.expand('${DatabaseConnection#ResponseAsXml#//ResultSet/Row['+i+']/PRODUCTDETAILS.PRICE}')
     def name = context.expand('${DatabaseConnection#ResponseAsXml#//ResultSet/Row['+i+']/PRODUCTDETAILS.NAME}')
     def metatitle = context.expand('${DatabaseConnection#ResponseAsXml#//ResultSet/Row['+i+']/PRODUCTDETAILS.METATITLE}')

	log.info "$model + $quantity + $price + $name + $metatitle"

      //Set values in ProductDetails properties
      testRunner.testCase.testSteps['ProductDetails'].setPropertyValue("model",model)
      testRunner.testCase.testSteps['ProductDetails'].setPropertyValue("quantity",quantity)
      testRunner.testCase.testSteps['ProductDetails'].setPropertyValue("price",price)
      testRunner.testCase.testSteps['ProductDetails'].setPropertyValue("name",name)
      testRunner.testCase.testSteps['ProductDetails'].setPropertyValue("metatitle",metatitle)

      //Run AddProduct request
      testRunner.runTestStepByName("AddProduct")

}

//Go to Logout step after all products are added
testRunner.gotoStepByName("Logout")

